                val filename = view.save_as_name.value
                val extension = view.save_as_extension.value

                if (filename.isEmpty()) {
                    activity.toast(com.amar.NoteDirector.R.string.filename_cannot_be_empty)
                    return@setOnClickListener
                }

                if (extension.isEmpty()) {
                    activity.toast(com.amar.NoteDirector.R.string.extension_cannot_be_empty)
                    return@setOnClickListener
                }

                val newFile = File(realPath, "$filename.$extension")
                if (!newFile.name.isAValidFilename()) {
                    activity.toast(com.amar.NoteDirector.R.string.filename_invalid_characters)
                    return@setOnClickListener
                }

                if (newFile.exists()) {
                    val title = String.format(activity.getString(com.amar.NoteDirector.R.string.file_already_exists_overwrite), newFile.name)
                    ConfirmationDialog(activity, title) {
                        callback(newFile.absolutePath)
                        dismiss()
                    }
                } else {
                    callback(newFile.absolutePath)
                    dismiss()
                }
            }) are unnecessary and can be removed.

